	if(ISTEST) upload_path = "C:\\Site\\wwwroot_dev\\Upload_File\\"; //°³¹ß

	
	upload_path = upload_path+"EI13\\";
		
	if(U_ID != ""){
		upload_path = upload_path + U_ID+"\\";
	} 
	else{
		upload_path = upload_path + "UnKnown\\"; //À¯Àú¾ÆÀÌµð°¡ ¾øÀ¸¸é UnKnownÀ¸·Î ³Ö¾îÁÜ.
	}
	
	return upload_path;
}

