function getValueFromParamWithIdOfTxt(Txt, id){ //Response·ÎºÎÅÍ °¢ °´Ã¼¸¦ ²ôÁý¾î ³½´Ù.
	var ent    = Chr(13)+Chr(10);
	var TxtArr = Split(Txt,ent);	
	
	for(var i=0;i<TxtArr.length;i++){				
		if(left(trim(TxtArr[i]),10)=="<param id="){
			var temp = trim(TxtArr[i]);
			//============ Id    ÃßÃâ =================
			var id_       = IndexOf(temp,"id")  +3;			
			var id_size   = IndexOf(temp,"type")-id_;
			//============ Type  ÃßÃâ =================
			var type_     = IndexOf(temp,"type")+5;			
			var type_size = IndexOf(temp,">")   -type_;
			//============ Value ÃßÃâ =================						
			var val_      = IndexOf(temp,">")   +1;						
			var val_size  = IndexOf(temp,"</",) -val_;
			
			if(trim(id) == trim(Substr(temp,id_, id_size))){
				return Replace(Substr(temp,val_, val_size),"&#32;"," "); //Æ¯¼ö¹®ÀÚ º¯°æÇØÁÜ.
			}	
		}
	}	
}

